+
+		if (precompiled) {
+			sourceMapCache.set(filename, path.join(cacheDir, `${precompiled}.js.map`));
+			return fs.readFileSync(path.join(cacheDir, `${precompiled}.js`), 'utf8');
+		}
+
+		return null;
+	});
