+		const touchedFiles = runner.saveSnapshotState();
+		if (touchedFiles) {
+			ipc.send({type: 'touched-files', files: touchedFiles});
+		}
+	} catch (err) {
+		ipc.send({type: 'internal-error', err: serializeError('Internal runner error', false, err)});
+		exit(1);
+		return;
+	}
