+  // os tmp directory.
+  expect(hgRepos).toHaveLength(2);
+  expect(hgRepos[0]).toMatch(/\/jest_changed_files_test_dir\/first_repo$/);
+  expect(hgRepos[1]).toMatch(/\/jest_changed_files_test_dir\/second_repo$/);
+});
