+      clearTimeout,
+      fail,
+      onComplete,
+      onException: () => {},
+      queueableFns: [{
+        fn: fnOne,
+      }, {
+        fn: fnTwo,
+      }],
+      setTimeout,
+    };
+    await queueRunner(options);
+    expect(fnOne).toHaveBeenCalled();
+    expect(fail).toHaveBeenCalled();
+    // Even if `fail` is called, the queue keeps running.
